    { id: 'mercado', name: 'Mercado', icon: 'basket' },
    { id: 'kiosco', name: 'Kiosco', icon: 'home' },
    { id: 'farmacia', name: 'Farmacia', icon: 'medical' },
    { id: 'supermercado', name: 'Supermercado', icon: 'business' },
  ];

  const restaurants = [
    {
      id: 1,
      name: 'Restaurante El Cubano',
      category: 'restaurante',
      rating: 4.8,
      deliveryTime: '20-30 min',
      deliveryFee: 15,
      minOrder: 100,
      image: 'üè™',
      products: [
        { id: 1, name: 'Ropa Vieja', price: 120, description: 'Carne desmechada con arroz y frijoles', image: 'üçñ' },
