        { id: 3, name: 'Pescado a la Plancha', price: 150, description: 'Pescado fresco con vegetales', image: 'üêü' },
        { id: 4, name: 'Arroz con Pollo', price: 90, description: 'Arroz amarillo con pollo y vegetales', image: 'üçö' },
      ]
    },
    {
      id: 2,
      name: 'Pizzer√≠a La Italiana',
      category: 'restaurante',
      rating: 4.6,
      deliveryTime: '25-35 min',
      deliveryFee: 20,
      minOrder: 80,
      image: 'üçï',
      products: [
        { id: 5, name: 'Pizza Margherita', price: 80, description: 'Tomate, mozzarella y albahaca', image: 'üçï' },
        { id: 6, name: 'Pizza Pepperoni', price: 95, description: 'Pepperoni y mozzarella', image: 'üçï' },
        { id: 7, name: 'Pizza Hawaiana', price: 90, description: 'Jam√≥n, pi√±a y mozzarella', image: 'üçï' },
        { id: 8, name: 'Lasa√±a', price: 110, description: 'Lasa√±a de carne con queso', image: 'üçù' },
      ]
    },
    {
      id: 3,
      name: 'Cafeter√≠a Central',
      category: 'tienda',
      rating: 4.7,
      deliveryTime: '15-25 min',
      deliveryFee: 10,
      minOrder: 50,
      image: '‚òï',
      products: [
        { id: 9, name: 'Caf√© Cubano', price: 25, description: 'Caf√© expreso tradicional', image: '‚òï' },
        { id: 10, name: 'Cortadito', price: 30, description: 'Caf√© con leche espumosa', image: '‚òï' },
        { id: 11, name: 'Sandwich Cubano', price: 65, description: 'Jam√≥n, queso, pepinillos y mostaza', image: 'ü•™' },
        { id: 12, name: 'Flan de Leche', price: 40, description: 'Flan casero con caramelo', image: 'üçÆ' },
      ]
    },
  ];

  const filteredRestaurants = restaurants.filter(restaurant => {
    const matchesCategory = selectedCategory === 'all' || restaurant.category === selectedCategory;
    const matchesSearch = restaurant.name.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const addToCart = (product, restaurant) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item => 
        item.id === product.id 
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1, restaurant: restaurant.name }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity === 0) {
      removeFromCart(productId);
    } else {
      setCart(cart.map(item => 
        item.id === productId 
          ? { ...item, quantity: newQuantity }
          : item
      ));
    }
  };

  const getCartTotal = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getCartItemCount = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const handleCheckout = () => {
    if (cart.length === 0) {
      Alert.alert('Carrito vac√≠o', 'Agrega productos al carrito para continuar');
      return;
    }

    const total = getCartTotal();
    const restaurant = restaurants.find(r => r.products.some(p => cart.some(c => c.id === p.id)));

    if (restaurant && total < restaurant.minOrder) {
      Alert.alert(
        'Pedido m√≠nimo',
        `El pedido m√≠nimo para ${restaurant.name} es $${restaurant.minOrder}. Tu pedido actual es $${total}.`
      );
      return;
    }

    Alert.alert(
      'Confirmar Pedido',
      `Total: $${total}\n¬øConfirmar pedido?`,
      [
        { text: 'Cancelar', style: 'cancel' },
        {
          text: 'Confirmar',
          onPress: () => {
            setCart([]);
            navigation.navigate('OrderTracking', { 
              orderId: Date.now(),
              type: 'food',
              restaurant: restaurant?.name,
              total: total
            });
          }
        }
      ]
    );
  };

  if (selectedRestaurant) {
    const restaurant = restaurants.find(r => r.id === selectedRestaurant);
    return (
      <View style={globalStyles.container}>
        {/* Header del restaurante */}
        <View style={styles.restaurantHeader}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => setSelectedRestaurant(null)}
          >
            <Ionicons name="arrow-back" size={24} color="#FFFFFF" />
          </TouchableOpacity>
          <View style={styles.restaurantInfo}>
            <Text style={styles.restaurantHeaderName}>{restaurant.name}</Text>
            <Text style={styles.restaurantHeaderDetails}>
              ‚≠ê {restaurant.rating} ‚Ä¢ üöö {restaurant.deliveryTime} ‚Ä¢ Env√≠o $${restaurant.deliveryFee}
            </Text>
          </View>
        </View>

        {/* Men√∫ de productos */}
        <ScrollView style={styles.menuContainer}>
          <Text style={styles.menuTitle}>Men√∫</Text>
          {restaurant.products.map((product) => {
            const cartItem = cart.find(item => item.id === product.id);
            return (
              <Card key={product.id} style={styles.productCard}>
                <Card.Content>
                  <View style={styles.productContent}>
                    <View style={styles.productImage}>
                      <Text style={styles.productEmoji}>{product.image}</Text>
                    </View>
                    <View style={styles.productDetails}>
                      <Text style={styles.productName}>{product.name}</Text>
                      <Text style={styles.productDescription}>{product.description}</Text>
                      <Text style={styles.productPrice}>${product.price}</Text>
                    </View>
                    <View style={styles.productActions}>
                      {cartItem ? (
                        <View style={styles.quantityControls}>
                          <TouchableOpacity
                            style={styles.quantityButton}
                            onPress={() => updateQuantity(product.id, cartItem.quantity - 1)}
                          >
                            <Ionicons name="remove" size={16} color="#FF6B35" />
                          </TouchableOpacity>
                          <Text style={styles.quantityText}>{cartItem.quantity}</Text>
                          <TouchableOpacity
                            style={styles.quantityButton}
                            onPress={() => updateQuantity(product.id, cartItem.quantity + 1)}
                          >
                            <Ionicons name="add" size={16} color="#FF6B35" />
                          </TouchableOpacity>
                        </View>
                      ) : (
                        <TouchableOpacity
                          style={styles.addButton}
                          onPress={() => addToCart(product, restaurant)}
                        >
                          <Ionicons name="add" size={20} color="#FFFFFF" />
                        </TouchableOpacity>
                      )}
                    </View>
                  </View>
                </Card.Content>
              </Card>
            );
          })}
        </ScrollView>

        {/* Carrito flotante */}
        {cart.length > 0 && (
          <View style={styles.cartFloating}>
            <TouchableOpacity style={styles.cartButton} onPress={handleCheckout}>
              <View style={styles.cartContent}>
                <View style={styles.cartInfo}>
                  <Text style={styles.cartText}>
                    {getCartItemCount()} productos ‚Ä¢ ${getCartTotal()}
                  </Text>
                </View>
                <Text style={styles.checkoutText}>Confirmar Pedido</Text>
              </View>
            </TouchableOpacity>
          </View>
        )}
      </View>
    );
  }

  return (
    <View style={globalStyles.container}>
      {/* Buscador */}
      <View style={styles.searchContainer}>
        <Searchbar
          placeholder="Buscar restaurantes..."
          onChangeText={setSearchQuery}
          value={searchQuery}
          style={styles.searchBar}
        />
      </View>

      {/* Categor√≠as */}
      <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.categoriesContainer}>
        {categories.map((category) => (
          <TouchableOpacity
            key={category.id}
            style={[
              styles.categoryButton,
              selectedCategory === category.id && styles.categoryButtonActive
            ]}
            onPress={() => setSelectedCategory(category.id)}
          >
            <Ionicons 
              name={category.icon} 
              size={20} 
              color={selectedCategory === category.id ? '#FFFFFF' : '#FF6B35'} 
            />
            <Text style={[
              styles.categoryText,
              selectedCategory === category.id && styles.categoryTextActive
            ]}>
              {category.name}
            </Text>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {/* Lista de restaurantes */}
      <ScrollView style={styles.restaurantsContainer}>
        {filteredRestaurants.map((restaurant) => (
          <TouchableOpacity
            key={restaurant.id}
            onPress={() => setSelectedRestaurant(restaurant.id)}
          >
            <Card style={styles.restaurantCard}>
              <Card.Content>
                <View style={styles.restaurantContent}>
                  <View style={styles.restaurantImageContainer}>
                    <Text style={styles.restaurantEmoji}>{restaurant.image}</Text>
                  </View>
                  <View style={styles.restaurantDetails}>
                    <Text style={styles.restaurantName}>{restaurant.name}</Text>
                    <View style={styles.restaurantMeta}>
                      <Text style={styles.restaurantRating}>‚≠ê {restaurant.rating}</Text>
                      <Text style={styles.restaurantTime}>üöö {restaurant.deliveryTime}</Text>
                    </View>
                    <Text style={styles.restaurantFee}>Env√≠o: ${restaurant.deliveryFee} ‚Ä¢ M√≠nimo: ${restaurant.minOrder}</Text>
                  </View>
                  <Ionicons name="chevron-forward" size={20} color="#CCCCCC" />
                </View>
              </Card.Content>
            </Card>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {/* Carrito flotante */}
      {cart.length > 0 && (
        <View style={styles.cartFloating}>
          <TouchableOpacity style={styles.cartButton} onPress={handleCheckout}>
            <View style={styles.cartContent}>
              <Badge style={styles.cartBadge}>{getCartItemCount()}</Badge>
              <Text style={styles.cartText}>Ver Carrito ‚Ä¢ ${getCartTotal()}</Text>
            </View>
          </TouchableOpacity>
        </View>
      )}
    </View>
  );
};

