import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Alert, ScrollView } from 'react-native';
import { TextInput, Button, Card } from 'react-native-paper';
import { Ionicons } from '@expo/vector-icons';
import { theme, styles as globalStyles } from '../theme/theme';
import { loginUser, validateEmail, USER_TYPES, signInWithGoogle } from '../services/authService';
import { useAuth } from '../context/AuthContext';

const LoginScreen = ({ navigation }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [emailError, setEmailError] = useState('');
  const [loading, setLoading] = useState(false);
  const { updateUser } = useAuth();

  const handleLogin = async () => {
    // Validar campos
    if (!email.trim()) {
      setEmailError('Por favor ingresa tu email');
      return;
    }

    if (!validateEmail(email)) {
      setEmailError('Por favor ingresa un email válido');
      return;
    }
    setEmailError('');

    if (!password.trim()) {
      Alert.alert('Error', 'Por favor ingresa tu contraseña');
      return;
    }

    if (password.length < 6) {
      Alert.alert('Error', 'La contraseña debe tener al menos 6 caracteres');
      return;
    }

    setLoading(true);

    try {
      const result = await loginUser(email.trim(), password);

      if (result.success) {
        // Actualizar el usuario en el contexto
        updateUser(result.user);

        // Navegar a la pantalla correspondiente según el tipo de usuario
        const userType = result.user.userType;
        let targetScreen = 'ClientMain'; // Por defecto

        if (userType === USER_TYPES.BUSINESS) {
          targetScreen = 'BusinessMain';
        } else if (userType === USER_TYPES.DRIVER) {
          targetScreen = 'DriverMain';
        }

        // Navegar y mostrar mensaje de bienvenida
        navigation.reset({
          index: 0,
          routes: [{ name: targetScreen }],
        });

        Alert.alert('Éxito', `¡Bienvenido ${result.user.name}!`);
      } else {
        Alert.alert('Error de inicio de sesión', result.error);
      }
    } catch (error) {
      console.error('Error during login:', error);
      Alert.alert('Error', 'Ha ocurrido un error inesperado. Intenta nuevamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <ScrollView style={globalStyles.container}>
      <View style={styles.container}>
        {/* Logo pequeño */}
        <View style={styles.logoContainer}>
          <Ionicons name="bicycle" size={60} color="#FF6B35" />
          <Text style={styles.logoText}>RAPIFLOW</Text>
        </View>

        {/* Formulario */}
        <View style={styles.formContainer}>
          <Text style={styles.title}>Iniciar Sesión</Text>

          <TextInput
            label="Correo electrónico"
            value={email}
            onChangeText={setEmail}
            mode="outlined"
            style={globalStyles.input}
            keyboardType="email-address"
            autoCapitalize="none"
            left={<TextInput.Icon icon="email" />}
            id="email-input"
            name="email"
            autoComplete="email"
          />

          <TextInput
            label="Contraseña"
            value={password}
            onChangeText={setPassword}
            mode="outlined"
            style={globalStyles.input}
            secureTextEntry={!showPassword}
            left={<TextInput.Icon icon="lock" />}
            right={
              <TextInput.Icon 
                icon={showPassword ? "eye-off" : "eye"}
                onPress={() => setShowPassword(!showPassword)}
              />
            }
            id="password-input"
            name="password"
            autoComplete="current-password"
          />

          <Button
            mode="contained"
            onPress={handleLogin}
            style={styles.loginButton}
            contentStyle={styles.buttonContent}
            loading={loading}
            disabled={loading}
          >
            {loading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
          </Button>

          <TouchableOpacity
            style={styles.registerLink}
            onPress={() => navigation.navigate('Register')}
          >
            <Text style={styles.registerText}>
              ¿No tienes cuenta? <Text style={styles.registerTextBold}>Regístrate</Text>
            </Text>
          </TouchableOpacity>
        </View>

        {/* Información de ayuda */}
        <Card style={styles.helpCard}>
          <Card.Content>
            <Text style={styles.helpTitle}>¿Necesitas ayuda?</Text>
            <TouchableOpacity 
              style={styles.helpItem}
              onPress={() => Alert.alert('Recuperar contraseña', 'Próximamente podrás recuperar tu contraseña por email.')}
            >
              <Ionicons name="key" size={16} color="#FF6B35" />
              <Text style={styles.helpText}>¿Olvidaste tu contraseña?</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={styles.helpItem}
              onPress={() => Alert.alert('Soporte', 'Contacta con nosotros:\nTeléfono: +53 5123 4567\nEmail: soporte@rapiflow.cu')}
            >
              <Ionicons name="help-circle" size={16} color="#FF6B35" />
              <Text style={styles.helpText}>Contactar soporte</Text>
            </TouchableOpacity>
          </Card.Content>
        </Card>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    justifyContent: 'center',
  },
  logoContainer: {
    alignItems: 'center',
    marginBottom: 40,
  },
  logoText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#FF6B35',
    marginTop: 10,
  },
  formContainer: {
    marginBottom: 30,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#333333',
    textAlign: 'center',
    marginBottom: 30,
  },
  loginButton: {
    marginTop: 20,
    marginBottom: 20,
  },
  buttonContent: {
    paddingVertical: 8,
  },
  registerLink: {
    alignItems: 'center',
    marginTop: 10,
  },
  registerText: {
    fontSize: 14,
    color: '#666666',
  },
  registerTextBold: {
    fontWeight: 'bold',
    color: '#FF6B35',
  },
  helpCard: {
    marginTop: 20,
    backgroundColor: '#fff',
    elevation: 2,
  },
  helpTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 12,
  },
  helpItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingVertical: 8,
  },
  helpText: {
    fontSize: 14,
    color: '#666',
    marginLeft: 8,
  },
});

export default LoginScreen;
