                mode="contained"
                onPress={() => {
                  console.log('Save button pressed');
                  saveProduct();
                }}
                style={styles.saveButton}
                labelStyle={styles.saveButtonText}
              >
                {editingProduct ? 'Actualizar' : 'Aceptar'}
